INSERT INTO public.document(title, text, date, time) VALUES ('Уимблдонский турнир 2009 в одиночном разряде среди девушек','В 2009 году победительницей британского турнира Большого шлема в одиночном разряде среди девушек стала представительница Таиланда Ноппаван Летчивакан.','31.10.2014','16:43:17');
INSERT INTO public.document(title, text, date, time) VALUES ('Ле Дриан, Жан-Ив','Жан-Ив Ле Дриан (фр. Jean-Yves Le Drian; род. 30 июня 1947 года, Лорьян, Франция) — французский государственный и политический деятель. Министр обороны Франции в кабинетах Жана-Марка Эро, Мануэля Вальса, Бернара Казнёва с 16 мая 2012 года по 17 мая 2017 года. Министр иностранных дел и международного сотрудничества Франции с 17 мая 2017 года в кабинете Эдуара Филиппа.','05.11.2016','21:58:09');
INSERT INTO public.document(title, text, date, time) VALUES ('Вессели, Жозефина','Жозефина Вессели (нем. Josephine Wessely; 18 марта 1860, Вена — 12 августа 1887, Карлсбад) — австрийская театральная актриса. Тётка актрисы Паулы Вессели.','23.12.2015','10:13:38');
