INSERT INTO public.document(title, text, date, time) VALUES ('Муби (язык)','Му́би (англ. mubi, moubi, minjile) — язык восточночадской ветви чадской семьи, распространённый в центральной части Чада в регионе Гера. Язык народа муби. Относится к группе языков муби. Численность говорящих — около 35 300 человек (1993). Язык бесписьменный.','10.09.2016','00:03:29');
