Ветер (фильм):','26.09.2015','17:29:40');
INSERT INTO public.document(title, text, date, time) VALUES ('Стрельцов, Игнат Андреевич','Игна́т Андре́евич Стрельцо́в (1851 — после 1917) — член I Государственной думы от Харьковской губернии, крестьянин.','11.01.2015','03:33:13');
