INSERT INTO public.document(title, text, date, time) VALUES ('Пресс-секретарь Белого дома','Пресс-секретарь Белого дома (англ. White House Press Secretary) — старшее должностное лицо Белого дома, исполняющее обязанности официального представителя президента США по общению с пресс-корпусом.','20.07.2017','18:47:07');
INSERT INTO public.document(title, text, date, time) VALUES ('Айносато-Коэн (станция)','Станция Айносато-Коэн (яп. あいの里公園駅 айносато коэн эки) — железнодорожная станция в японском городе Саппоро, обслуживаемая компанией JR Hokkaido. На этой станции можно использовать Kitaca (смарт-карта).','11.12.2016','14:03:05');
